import logo from './logo.svg';
import './App.css';
import {useState} from 'react';
import axios from 'axios';
function App() {

  const [pokemonName, setPokemonName] = useState("");
  const [pokemon, setPokemon] = useState({});

  const searchPokemon = () => {axios.get(`https://pokeapi.co/api/v2/pokemon/${pokemonName}`).then((response) =>{
      setPokemon({name: pokemonName, 
        species: response.data.species.name, 
        img: response.data.sprites.front_default, 
        hp: response.data.stats[0].base/stats})
    });
  };

  return (
    <div className="App">
      <div className="App-header">
        <img src="./logo.svg"/>
        <h3> POKEMON </h3>
        <input type="text" onChange={(event) => {setPokemonName(event.target.value)}}/>
        <button onClick={searchPokemon}> Search Pokemon </button>
      </div>
    </div>
  );
}

export default App;
