import logo from './logo.svg';
import './App.css';
import {useState} from 'react';
import Axios from 'axios';
function App() {

  const [pokemonName, setPokemonName] = useState("");
  const [pokemonChosen, setPokemonChosen] = useState(false);
  const [pokemon, setPokemon] = useState({
    name: "", 
    species: "", 
    img: "", 
    hp: "",
    attack: "",
    defense: "",
    type: ""
  });

  const searchPokemon = () => {Axios.get(`https://pokeapi.co/api/v2/pokemon/${pokemonName}`).then(
    (response) =>{
      setPokemon({
        name: pokemonName, 
        species: response.data.species.name, 
        img: response.data.sprites.front_default, 
        hp: response.data.stats[0].base_stat,
        attack: response.data.stats[1].base_stat,
        defense: response.data.stats[2].base_stat,
        type: response.data.types[0].type.name
      });
      setPokemonChosen(true);
    });
  };

  return (
    <div className="App">
      <div className="App-header row">
        <img src="./logo.svg"/>
        <h3> POKEMON </h3>
        <input type="text" onChange={(event) => {setPokemonName(event.target.value)}}/>
        <button onClick={searchPokemon}> Search Pokemon </button>

      </div>
      <div className="display">
        {!pokemonChosen?(<h3>Please choose a Pokemon</h3>):(
        <>
        <div className='col-4'>
          <h3>{pokemon.name}</h3>
          <h4>Species: {pokemon.species}</h4>
          <h4>Type: {pokemon.type}</h4>
          <h5>HP: {pokemon.hp}</h5>
          <h5>Attack: {pokemon.attack}</h5>
          <h5>Defense: {pokemon.defense}</h5>
        </div>
        <div className='col-8'>
          <img src={pokemon.img} />
        </div>
        </>
        )}
      </div>
    </div>
  );
}

export default App;
